contracts/Diamond.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibDiamond, IDiamondCut } from "./libraries/LibDiamond.sol";

/**
 * @title Diamond
 * @dev The main Diamond proxy contract (EIP-2535)
 * 
 * This is the entry point for all calls to the diamond.
 * It uses the fallback function to delegatecall to the appropriate facet.
 * 
 * Key Features:
 * 1. Single entry point for all facet functions
 * 2. Uses delegatecall to preserve msg.sender and msg.value
 * 3. Storage is shared across all facets
 * 4. No limit on contract size (24KB limit bypassed)
 * 
 * Best Practices:
 * 1. Initialize all facets in constructor or via diamondCut
 * 2. Use proper access control on diamondCut
 * 3. Consider implementing a pause mechanism
 * 4. Test thoroughly as bugs affect all facets
 */
contract Diamond {

    error FunctionNotFound(bytes4 _functionSelector);

    /**
     * @dev Constructor sets up the diamond with initial facets
     * @param _contractOwner The address that will be the owner of the diamond
     * @param _diamondCut Initial facets to add to the diamond
     */
    constructor(
        address _contractOwner,
        IDiamondCut.FacetCut[] memory _diamondCut
    ) payable {
        LibDiamond.setContractOwner(_contractOwner);
        LibDiamond.diamondCut(_diamondCut, address(0), "");
    }

    /**
     * @dev Fallback function that delegates calls to facets
     * 
     * How it works:
     * 1. Look up the facet address for the called function selector
     * 2. If no facet is found, revert with FunctionNotFound
     * 3. Delegatecall to the facet
     * 4. Return any return data or revert with error
     */
    fallback() external payable {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();

        // Get facet from function selector
        address facet = ds.selectorToFacetAndPosition[msg.sig].facetAddress;

        if (facet == address(0)) {
            revert FunctionNotFound(msg.sig);
        }

        // Execute external function from facet using delegatecall
        // This preserves msg.sender, msg.value, and uses diamond's storage
        assembly {
            // Copy function selector and any arguments
            calldatacopy(0, 0, calldatasize())

            // Execute function call using the facet
            let result := delegatecall(gas(), facet, 0, calldatasize(), 0, 0)

            // Get any return value
            returndatacopy(0, 0, returndatasize())

            // Return any return value or revert if call failed
            switch result
            case 0 {
                revert(0, returndatasize())
            }
            default {
                return(0, returndatasize())
            }
        }
    }

    /**
     * @dev Receive function to accept ETH transfers
     */
    receive() external payable {}
}

contracts/facets/CounterFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibAppStorage } from "../libraries/LibAppStorage.sol";

/**
 * @title CounterFacet
 * @dev Simple example facet demonstrating the diamond pattern
 * 
 * This facet provides basic counter functionality to show
 * how facets can read and write to shared storage.
 */
contract CounterFacet {

    event CounterIncremented(uint256 newValue);
    event CounterDecremented(uint256 newValue);
    event CounterReset();

    /**
     * @notice Get the current counter value
     * @return The counter value
     */
    function getCounter() external view returns (uint256) {
        return LibAppStorage.appStorage().counter;
    }

    /**
     * @notice Increment the counter by 1
     */
    function increment() external {
        LibAppStorage.AppStorage storage s = LibAppStorage.appStorage();
        s.counter += 1;
        emit CounterIncremented(s.counter);
    }

    /**
     * @notice Decrement the counter by 1
     */
    function decrement() external {
        LibAppStorage.AppStorage storage s = LibAppStorage.appStorage();
        require(s.counter > 0, "Counter: cannot decrement below zero");
        s.counter -= 1;
        emit CounterDecremented(s.counter);
    }

    /**
     * @notice Increment the counter by a specific amount
     * @param _amount The amount to increment by
     */
    function incrementBy(uint256 _amount) external {
        LibAppStorage.AppStorage storage s = LibAppStorage.appStorage();
        s.counter += _amount;
        emit CounterIncremented(s.counter);
    }

    /**
     * @notice Reset the counter to zero
     */
    function resetCounter() external {
        LibAppStorage.appStorage().counter = 0;
        emit CounterReset();
    }
}

contracts/facets/DiamondCutFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibDiamond, IDiamondCut } from "../libraries/LibDiamond.sol";

/**
 * @title DiamondCutFacet
 * @dev Facet for adding, replacing, and removing functions in the diamond
 * 
 * This is a critical facet that should be carefully protected.
 * Only the contract owner can call diamondCut.
 * 
 * Best Practices:
 * 1. Always verify the facet addresses have code before adding
 * 2. Consider using a timelock for production diamonds
 * 3. Emit events for all changes for transparency
 */
contract DiamondCutFacet is IDiamondCut {

    /**
     * @notice Add/replace/remove any number of functions and optionally execute
     *         a function with delegatecall
     * @param _diamondCut Contains the facet addresses and function selectors
     * @param _init The address of the contract or facet to execute _calldata
     * @param _calldata A function call, including function selector and arguments
     *                  _calldata is executed with delegatecall on _init
     */
    function diamondCut(
        FacetCut[] calldata _diamondCut,
        address _init,
        bytes calldata _calldata
    ) external {
        // Only owner can make changes
        LibDiamond.enforceIsContractOwner();
        LibDiamond.diamondCut(_diamondCut, _init, _calldata);
    }
}

contracts/facets/DiamondLoupeFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibDiamond } from "../libraries/LibDiamond.sol";

/**
 * @title IDiamondLoupe
 * @dev Standard interface for the Diamond Loupe
 * See: https://eips.ethereum.org/EIPS/eip-2535
 */
interface IDiamondLoupe {
    struct Facet {
        address facetAddress;
        bytes4[] functionSelectors;
    }

    function facets() external view returns (Facet[] memory facets_);
    function facetFunctionSelectors(address _facet) external view returns (bytes4[] memory facetFunctionSelectors_);
    function facetAddresses() external view returns (address[] memory facetAddresses_);
    function facetAddress(bytes4 _functionSelector) external view returns (address facetAddress_);
}

/**
 * @title DiamondLoupeFacet
 * @dev Implements IDiamondLoupe for introspection of the diamond
 * 
 * The Loupe allows external contracts and users to query:
 * - All facets and their selectors
 * - Which facet handles a specific function
 * - All functions a facet provides
 * 
 * This is essential for transparency and verification of diamond state.
 */
contract DiamondLoupeFacet is IDiamondLoupe {

    /**
     * @notice Gets all facet addresses and their four byte function selectors
     * @return facets_ Array of Facet structs
     */
    function facets() external view override returns (Facet[] memory facets_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        uint256 numFacets = ds.facetAddresses.length;
        facets_ = new Facet[](numFacets);

        for (uint256 i; i < numFacets; i++) {
            address facetAddress_ = ds.facetAddresses[i];
            facets_[i].facetAddress = facetAddress_;
            facets_[i].functionSelectors = ds.facetFunctionSelectors[facetAddress_].functionSelectors;
        }
    }

    /**
     * @notice Gets all the function selectors provided by a facet
     * @param _facet The facet address
     * @return facetFunctionSelectors_ Array of function selectors
     */
    function facetFunctionSelectors(address _facet) 
        external 
        view 
        override 
        returns (bytes4[] memory facetFunctionSelectors_) 
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetFunctionSelectors_ = ds.facetFunctionSelectors[_facet].functionSelectors;
    }

    /**
     * @notice Get all the facet addresses used by a diamond
     * @return facetAddresses_ Array of facet addresses
     */
    function facetAddresses() external view override returns (address[] memory facetAddresses_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetAddresses_ = ds.facetAddresses;
    }

    /**
     * @notice Gets the facet that supports the given selector
     * @dev If facet is not found return address(0)
     * @param _functionSelector The function selector
     * @return facetAddress_ The facet address
     */
    function facetAddress(bytes4 _functionSelector) 
        external 
        view 
        override 
        returns (address facetAddress_) 
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetAddress_ = ds.selectorToFacetAndPosition[_functionSelector].facetAddress;
    }
}

contracts/facets/ERC20Facet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibAppStorage } from "../libraries/LibAppStorage.sol";
import { LibDiamond } from "../libraries/LibDiamond.sol";

/**
 * @title ERC20Facet
 * @dev ERC20 token implementation as a diamond facet
 * 
 * Demonstrates how a full token standard can be implemented
 * as a facet. All storage is in LibAppStorage.
 * 
 * Best Practices:
 * 1. Functions are stateless - all state in storage lib
 * 2. Can be upgraded without affecting token balances
 * 3. Shares storage with other facets safely
 */
contract ERC20Facet {

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    error ERC20InsufficientBalance(address sender, uint256 balance, uint256 needed);
    error ERC20InvalidSender(address sender);
    error ERC20InvalidReceiver(address receiver);
    error ERC20InsufficientAllowance(address spender, uint256 allowance, uint256 needed);
    error ERC20InvalidApprover(address approver);
    error ERC20InvalidSpender(address spender);

    /**
     * @notice Initialize the ERC20 token (can only be called once)
     * @param _name Token name
     * @param _symbol Token symbol
     * @param _decimals Token decimals
     */
    function initializeERC20(
        string calldata _name,
        string calldata _symbol,
        uint8 _decimals
    ) external {
        LibDiamond.enforceIsContractOwner();
        LibAppStorage.AppStorage storage s = LibAppStorage.appStorage();
        require(!s.initialized, "ERC20: already initialized");

        s.name = _name;
        s.symbol = _symbol;
        s.decimals = _decimals;
        s.initialized = true;
    }

    /**
     * @notice Returns the name of the token
     */
    function name() external view returns (string memory) {
        return LibAppStorage.appStorage().name;
    }

    /**
     * @notice Returns the symbol of the token
     */
    function symbol() external view returns (string memory) {
        return LibAppStorage.appStorage().symbol;
    }

    /**
     * @notice Returns the decimals of the token
     */
    function decimals() external view returns (uint8) {
        return LibAppStorage.appStorage().decimals;
    }

    /**
     * @notice Returns the total supply of the token
     */
    function totalSupply() external view returns (uint256) {
        return LibAppStorage.appStorage().totalSupply;
    }

    /**
     * @notice Returns the balance of an account
     */
    function balanceOf(address _account) external view returns (uint256) {
        return LibAppStorage.appStorage().balances[_account];
    }

    /**
     * @notice Transfer tokens to a recipient
     */
    function transfer(address _to, uint256 _value) external returns (bool) {
        _transfer(msg.sender, _to, _value);
        return true;
    }

    /**
     * @notice Get the allowance of a spender for an owner
     */
    function allowance(address _owner, address _spender) external view returns (uint256) {
        return LibAppStorage.appStorage().allowances[_owner][_spender];
    }

    /**
     * @notice Approve a spender to spend tokens
     */
    function approve(address _spender, uint256 _value) external returns (bool) {
        _approve(msg.sender, _spender, _value);
        return true;
    }

    /**
     * @notice Transfer tokens from one address to another
     */
    function transferFrom(address _from, address _to, uint256 _value) external returns (bool) {
        LibAppStorage.AppStorage storage s = LibAppStorage.appStorage();
        uint256 currentAllowance = s.allowances[_from][msg.sender];

        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < _value) {
                revert ERC20InsufficientAllowance(msg.sender, currentAllowance, _value);
            }
            unchecked {
                _approve(_from, msg.sender, currentAllowance - _value);
            }
        }

        _transfer(_from, _to, _value);
        return true;
    }

    /**
     * @notice Mint new tokens (only owner)
     */
    function mint(address _to, uint256 _amount) external {
        LibDiamond.enforceIsContractOwner();
        _mint(_to, _amount);
    }

    /**
     * @notice Burn tokens from an account
     */
    function burn(uint256 _amount) external {
        _burn(msg.sender, _amount);
    }

    // =============================================================
    //                     INTERNAL FUNCTIONS
    // =============================================================

    function _transfer(address _from, address _to, uint256 _value) internal {
        if (_from == address(0)) {
            revert ERC20InvalidSender(address(0));
        }
        if (_to == address(0)) {
            revert ERC20InvalidReceiver(address(0));
        }

        LibAppStorage.AppStorage storage s = LibAppStorage.appStorage();

        uint256 fromBalance = s.balances[_from];
        if (fromBalance < _value) {
            revert ERC20InsufficientBalance(_from, fromBalance, _value);
        }

        unchecked {
            s.balances[_from] = fromBalance - _value;
            s.balances[_to] += _value;
        }

        emit Transfer(_from, _to, _value);
    }

    function _mint(address _account, uint256 _value) internal {
        if (_account == address(0)) {
            revert ERC20InvalidReceiver(address(0));
        }

        LibAppStorage.AppStorage storage s = LibAppStorage.appStorage();
        s.totalSupply += _value;

        unchecked {
            s.balances[_account] += _value;
        }

        emit Transfer(address(0), _account, _value);
    }

    function _burn(address _account, uint256 _value) internal {
        if (_account == address(0)) {
            revert ERC20InvalidSender(address(0));
        }

        LibAppStorage.AppStorage storage s = LibAppStorage.appStorage();

        uint256 accountBalance = s.balances[_account];
        if (accountBalance < _value) {
            revert ERC20InsufficientBalance(_account, accountBalance, _value);
        }

        unchecked {
            s.balances[_account] = accountBalance - _value;
            s.totalSupply -= _value;
        }

        emit Transfer(_account, address(0), _value);
    }

    function _approve(address _owner, address _spender, uint256 _value) internal {
        if (_owner == address(0)) {
            revert ERC20InvalidApprover(address(0));
        }
        if (_spender == address(0)) {
            revert ERC20InvalidSpender(address(0));
        }

        LibAppStorage.appStorage().allowances[_owner][_spender] = _value;
        emit Approval(_owner, _spender, _value);
    }
}

contracts/facets/OwnershipFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibDiamond } from "../libraries/LibDiamond.sol";

/**
 * @title OwnershipFacet
 * @dev Facet for managing diamond ownership
 * 
 * Provides ownership transfer functionality.
 * The owner can add/remove facets via DiamondCutFacet.
 */
contract OwnershipFacet {

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @notice Get the current owner of the diamond
     * @return owner_ The owner address
     */
    function owner() external view returns (address owner_) {
        owner_ = LibDiamond.contractOwner();
    }

    /**
     * @notice Transfer ownership to a new address
     * @param _newOwner The new owner's address
     */
    function transferOwnership(address _newOwner) external {
        LibDiamond.enforceIsContractOwner();
        LibDiamond.setContractOwner(_newOwner);
    }
}

